<?php 	include("koneksi.php"); 		$FILTER_FIELD_NAME 	= "nama_barang";	$TABLE_BARANG 			= "barang";	$LIMIT_DATA					= 10;		if( isset( $_GET['q'] ) ) {			$searchKey = $_GET['q'];			$QUERY = sprintf("SELECT * FROM %s WHERE %s LIKE  '% %s %' LIMIT %d", $TABLE_BARANG, $FILTER_FIELD_NAME, $LIMIT_DATA);			} else {		$QUERY = sprintf("SELECT * FROM %s LIMIT %d", $TABLE_BARANG, $LIMIT_DATA );	}		$dataTable = array();		$q = mysql_query($QUERY);		if(!$q) {		$message = array("msg" => "Gagal");		json_encode($message, true);		print_r(json_encode($message, true));		//Informasi yg dikirimkan kepada client	}		while($data = mysql_fetch_object($q)){		$dataTable[] = $data; // Cara Simpel make mysql_fetch_object	}		header('Content-type: application/json');	$data = " {  \"barang\" : " . json_encode($dataTable, true) . " } ";			print_r($data);		//Informasi yg dikirimkan kepada client			    	?>